import { useEffect, useState } from "react";
import styled, { css } from "styled-components";

const flexCenter = css`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MainLayout = css`
  box-sizing: border-box; 
  height: 100%;
  padding: 40px;
  width: 33.3%;
  border-right: 1px solid black;
`;

const Wrap = styled.div`
  height: 100vh;
  width: 100vw;
`;

const MainContent = styled.div`
 ${ MainLayout }

`;

const GreetingStyle = styled.div`
  align-items: center;
  display: flex;
  justify-content: space-between;
`;

const UserInput = styled.input`
  border-width: 0px 0px 1px 0px;
  font-size: 30px;
  height: 60px;
  width: 70%;
`;

const InputButton = styled.div`
  ${ flexCenter }
  border: 1px solid black;
  border-radius: 10px;
  height: 30px;
  width: 20%;
  min-width: 60px;
`;

const UserName = styled.div`
  display: userName !== '' ? flex : none;
  font-size: 30px;
  width: 100%;
`;


const ClockCon = styled.div`
  ${flexCenter}
  width: 50%;
`;

const ClockStyle = styled.div`
  font-size: 24px;
  font-weight: bold;
`;

const ListStyle = styled.div`

`;

function Dashboard() {
  const [time, setTime] = useState(new Date());
  const [userName, setUserName] = useState('');
  const [user, setUser] = useState('');
  const [toDo, setToDo] = useState('');
  const [toDoList, setToDoList] = useState([]);
  const [currKey, setCurrKey] = useState(0);

  useEffect(() => {
    const id = setInterval(() => {
      setTime(new Date());
    }, 1000);

    return (() => 
      clearInterval(id) // 불필요한 작업 방지
    ); 
  }, []);

  const getUserName = (e) => {
    setUserName(e.target.value);
  };

  const getTodo = (e) => {
    setToDo(e.target.value);
  };

  const submitUserName = (e) => {
    e.preventDefault();
    if(userName === '') {
        alert('입력창이 비었네요!');
    } else {
      setUser(userName);
      setUserName('');
    }
  };

  const submitToDo = (e) => {
    e.preventDefault();

    const addToDo = toDoList.concat({
      content: toDo,
      key: currKey,
    });

    setCurrKey(currKey + 1);
    setToDoList(addToDo);
    setToDo('');
  };

  const toDoListCon = toDoList.map((thisResult) => (
    <ListStyle key={ thisResult.key }>{ thisResult.content }</ListStyle>
  ));

  return (
    <Wrap>
      <MainContent>
        {
          user !== '' ? 
          <GreetingStyle>
            <UserName>{ user }님 <br />안녕하세요!</UserName>
          </GreetingStyle> : 
          <GreetingStyle>
            <UserInput 
              onChange={ getUserName } 
              placeholder="이름" 
              value={ userName } 
            />
            <InputButton onClick={ submitUserName }>저장</InputButton>
          </GreetingStyle>
        }
        <div>
          오늘은<br />{ time.toDateString() }
        </div>
      </MainContent>


      {/* <ClockCon>
        <ClockStyle>{ time.toLocaleString() }</ClockStyle>
      </ClockCon>


      <div>
        <GreetingStyle>
          <UserInput 
          onChange={ getTodo } 
          placeholder="할 일" 
          value={ toDo } 
        />
          <InputButton onClick={ submitToDo }>저장</InputButton>
        </GreetingStyle>
        <div>{ toDoListCon }</div>
      </div> */}
    </Wrap>

  );
}

export default Dashboard;
