import { useEffect, useState } from "react";
import styled, { css } from "styled-components";
import GreetingPart from "../components/GreetingPart";

const MainLayout = css`
  box-sizing: border-box; 
  height: 100%;
  padding: 40px;
  width: 33.3%;
  border-right: 1px solid black;
`;

const AlignCenter = css`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Wrap = styled.div`
  display: flex;
  height: 100vh;
  width: 100vw;
`;

const MainContent = styled.div`
 ${ MainLayout }
`;

const ContentBox = styled.div`
  font-size: 28px;
  height: 33.3%;
  width: 100%;
`;

const HorizontalAlign = styled.div`
  align-items: center;
  display: flex;
  justify-content: space-between;
`;

const InputArea = styled.input`
  border-width: 0px 0px 1px 0px;
  font-size: 30px;
  height: 60px;
  width: 70%;
`;

const InputButton = styled.div`
  ${ AlignCenter }
  border: 1px solid black;
  border-radius: 10px;
  font-size: 22px;
  height: 30px;
  width: 20%;
  min-width: 60px;
`;

const ListStyle = styled.div`

`;

function Dashboard() {
  const [time, setTime] = useState(new Date());
  const [currWeather, setCurrWeather] = useState(null);
  const [userName, setUserName] = useState('');
  const [user, setUser] = useState('');
  const [toDo, setToDo] = useState('');
  const [toDoList, setToDoList] = useState([]);
  const [currKey, setCurrKey] = useState(0);

  const getCurrentLocation = () => {
    // 현재 위치 가져오기
    navigator.geolocation.getCurrentPosition((position) => {
      let latitude = position.coords.latitude;
      let longitude = position.coords.longitude;

      getWeatherByCurrentLocation(latitude, longitude);
    });
  };

   // 현재 위치 날씨 API 가져오기
   const getWeatherByCurrentLocation = async (latitude, longitude) => {
    let WEATHER_API_KEY = '739cddaed6d840ce7ff865f1c8296393';
    let WEATHER_API_URL = `https://api.openweathermap.org/data/2.5/onecall?lat=${latitude}&lon=${longitude}&exclude={part}&appid=${WEATHER_API_KEY}&units=metric&lang=kr`;
    let response = await fetch(WEATHER_API_URL);
    let data = await response.json();
    setCurrWeather(data.current);
    // console.log(data);
  };

  useEffect(() => {
    getCurrentLocation();
    getWeatherByCurrentLocation();

  }, []);

  useEffect(() => {
    const id = setInterval(() => {
      setTime(new Date());
    }, 1000);

    return (() => 
      clearInterval(id) // 불필요한 작업 방지
    ); 
  }, []);

  const getUserName = (e) => {
    setUserName(e.target.value);
  };

  const getTodo = (e) => {
    setToDo(e.target.value);
  };

  const submitUserName = (e) => {
    e.preventDefault();
    if(userName === '') {
        alert('입력창이 비었네요!');
    } else {
      setUser(userName);
      setUserName('');
    }
  };

  const submitToDo = (e) => {
    e.preventDefault();

    const addToDo = toDoList.concat({
      content: toDo,
      key: currKey,
    });

    setCurrKey(currKey + 1);
    setToDoList(addToDo);
    setToDo('');
  };

  const toDoListCon = toDoList.map((thisResult) => (
    <ListStyle key={ thisResult.key }>{ thisResult.content }</ListStyle>
  ));

  return (
    <Wrap>
      <GreetingPart />
{/*   
      <MainContent>
        <HorizontalAlign>
          <InputArea 
            onChange={ getTodo } 
            placeholder="할 일" 
            value={ toDo } 
          />
          <InputButton onClick={ submitToDo }>저장</InputButton>
        </HorizontalAlign>
        <div>{ toDoListCon }</div>
      </MainContent>  */}
    </Wrap>

  );
}

export default Dashboard;
