import { useEffect, useState } from "react";
import styled, { css } from "styled-components";

const MainLayout = css`
  box-sizing: border-box; 
  height: 100%;
  padding: 40px;
  width: 33.3%;
  border-right: 1px solid black;
`;

const AlignCenter = css`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MainContent = styled.div`
    ${ MainLayout }
`;

const HorizontalAlign = styled.div`
  align-items: center;
  display: flex;
  justify-content: space-between;
`;

const InputArea = styled.input`
  border-width: 0px 0px 1px 0px;
  font-size: 30px;
  height: 60px;
  width: 70%;
`;

const InputButton = styled.div`
  ${ AlignCenter }
  border: 1px solid black;
  border-radius: 10px;
  font-size: 22px;
  height: 30px;
  width: 20%;
  min-width: 60px;
`;

const ListStyle = styled.div`

`;


function ToDoPart() {
    const [toDo, setToDo] = useState('');
    const [toDoList, setToDoList] = useState([]);
    const [currKey, setCurrKey] = useState(0);


    const getTodo = (e) => {
      setToDo(e.target.value);
    };

    const submitToDo = (e) => {
      e.preventDefault();

      const addToDo = toDoList.concat({
        content: toDo,
        key: currKey,
      });

      setCurrKey(currKey + 1);
      setToDoList(addToDo);
      setToDo('');
    };

    const toDoListCon = toDoList.map((thisResult) => (
      <ListStyle key={ thisResult.key }>{ thisResult.content }</ListStyle>
    ));

    return (
        <MainContent>
          <HorizontalAlign>
            <InputArea 
              onChange={ getTodo } 
              placeholder="할 일" 
              value={ toDo } 
            />
            <InputButton onClick={ submitToDo }>저장</InputButton>
          </HorizontalAlign>
          <div>{ toDoListCon }</div>
        </MainContent> 
    );
};

export default ToDoPart;
